    private static boolean isPalindrome(String s) {
        int beg = 0;
        int end = s.length() - 1;
        while (beg  < end) {
            if (s.charAt(beg) != s.charAt(end)) {
            	System.out.println("---:false");
                return false;
            }
            beg++;
            end--;
        }
        return true;
    }

    public static boolean isPalindrome5(String s) {
        if(s == null) return false;
        int len = s.length();
        if(len == 0 ) return true;
        int left = 0;
        int right = len -1;
        while(left < right){
            if(!(Character.isDigit(s.charAt(left)) || Character.isLetter(s.charAt(left)))   ){
                left ++;
                continue;
            }
            if( !(Character.isDigit(s.charAt(right)) || Character.isLetter(s.charAt(right)))   ){
                right --;
                continue;
            }
            
            if(Character.toLowerCase(s.charAt(left)) != Character.toLowerCase(s.charAt(right))){
                    return false;
            }
            left ++;
            right --;
        }
        return true;
    }

    private static char get(String s, int i) {
        if(i % 2 == 0)
            return '#';
        else 
            return s.charAt(i / 2);
    }
    
