    public static  String shortestPalindrome(String s) {	
        if(s==null || s.length() ==0 ) return "";
        int n = s.length();
        if(isPalindrome(s)) return s;
        //i from 0--2n; mid = n;
        StringBuilder prefix = new StringBuilder();
        for(int mid = n/2-1; mid >= 0; mid--){
//        	System.out.println("mid1: "+ mid);
            int count = 1;
            while( mid-count >= 0 && mid < n && s.charAt(mid - count)== s.charAt(mid + count)){
                count++;
            }
            count--;
            if(mid-count == 0){
//            	System.out.println("mid2: "+ mid);
                count++;
                while(mid+count<n){
//                	System.out.println("prefix: "+ prefix);
                	prefix.insert(0, s.charAt(mid+count));
                    count ++;
                }
                break;
            }
        }
        String r = prefix.toString()+s;
        System.out.println("r len:"+r.length());
        System.out.println("is: "+isPalindrome(r));
        return r;
    }

