    public static  String shortestPalindrome77(String s) {
        if(s==null || s.length() ==0 ) return "";
        int n = s.length();
        if(isPalindrome(s)) return s;
        //i from 0--2n; mid = n;
        StringBuilder prefix = new StringBuilder();
        for(int mid = n; mid > 0; mid--){
        	System.out.println("mid1: "+ mid);
            int count = 1;
            while( mid-count >= 0 && mid <= 2*n && get(s, mid - count)== get(s, mid + count)){
                count++;
            }
            count--;
            if(mid-count == 0){
//            	System.out.println("mid2: "+ mid);
                count++;
                while(mid+count<=2*n){
//                	System.out.println("prefix: "+ prefix);
                	prefix.insert(0, get(s, mid+count));
                    count +=2;
                }
                break;
            }
        }
        String r = prefix.toString()+s;
        System.out.println(""+isPalindrome(r));
        return r;
    }


